  methods: {
    clickItem (item, router) {
      const body = document.body
      const menu = body.querySelector('.top-tool')
      const activeItem = menu.querySelector('.opt')
      if (activeItem === item.target) {
        return
      }
      this.$router.push(router)
      item.target.classList.add('opt')
      activeItem.className = 'tool'
    }
  }
}
